useEffect(() => {
    if (map.current) return; // stops map from initializing more than once

    map.current = new maplibregl.Map({
        container: mapContainer.current,
        style: `https://api.maptiler.com/maps/streets-v2/style.json?key=${API_KEY}`,
        center: [lng, lat],
        zoom: zoom
    });

    // Add control
    map.current.addControl(new maplibregl.NavigationControl(), 'top-right');

    // Fetch commissions from your API
    fetch("http://localhost:8800/commission")
        .then(response => response.json())
        .then(commissions => {
            // Loop through commissions and create markers
            commissions.forEach(commission => {
                new maplibregl.Marker({ color: "#FF0000" })
                    .setLngLat([commission.commissionLongitude, commission.commissionLatitude])
                    .setPopup(new maplibregl.Popup().setHTML(`<h3>${commission.commissionTitle}</h3>`))
                    .addTo(map.current);
            });
        })
        .catch(error => {
            console.error(error);
        });
}, [API_KEY, lng, lat, zoom]);
