import React, {useState} from 'react'
import axios from 'axios'
import { useNavigate, Link } from 'react-router-dom'

const PostCommission = () => {
  const [commission, setCommission] = useState({
    comTitle: '',
    comDeadline: '',
    comLocation: '',
    comType: '',
    comDescription: '',
    comPay: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCommission((prev) => ({ ...prev, [name]: value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://192.168.1.47:8800/commission', commission);
      navigate('/commission-list');
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className='container'>
      <nav>
        <Link to='/accounts'>BACK</Link>
        <Link to='/'>HOME</Link>
      </nav>
      <h1>Post Commission</h1>
      <div className='form-group'>
        <label>
          Commission Title
          <input
            type='text'
            className='form-control'
            placeholder='Commission Title'
            onChange={handleChange}
            name='comTitle'
          />
        </label>
      </div>
      {/* ... other form fields ... */}
      <button className='btn btn-primary' onClick={handleClick}>
        POST
      </button>
    </div>
  );
};

export default PostCommission;
