import * as maptilersdk from '@maptiler/sdk';

const urlParams = new URLSearchParams(window.location.search);
maptilersdk.config.apiKey = 'ZQyqv6eWtI6zNE29SPDd';
const map = new maptilersdk.Map({
  container: 'map', // container's id or the HTML element to render the map
  style: maptilersdk.MapStyle.STREETS,
  center: [123.8854, 10.3157], // starting position [lng, lat]
  zoom: 10, // starting zoom
});
map.on('load', async () => {
    map.addSource('search-results', {
        type: 'geojson',
        data: {
            "type": "FeatureCollection",
            "features": []
        }
    });
    map.addLayer({
        'id': 'point-result',
        'type': 'circle',
        'source': 'search-results',
        'paint': {
            'circle-radius': 8,
            'circle-color': '#B42222',
            'circle-opacity': 0.5
        },
        'filter': ['==', '$type', 'Point']
    });
  });
  if (urlParams.get('q')) {
    const results = await maptilersdk.geocoding.forward(urlParams.get('q'));
  }
  if (urlParams.get('q')) {
    const results = await searchPlace(urlParams.get('q'), key);
    map.getSource('search-results').setData(results);
    if (results.features[0]) {
      map.fitBounds(results.features[0].bbox, {maxZoom: 19})
    }
  }
