import React, { useRef, useEffect, useState } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import './map.css';

export default function Map() {
    const mapContainer = useRef(null);
    const map = useRef(null);
    const [lng] = useState(123.8854);
    const [lat] = useState(10.3157);
    const [zoom] = useState(10);
    const [API_KEY] = useState('ZQyqv6eWtI6zNE29SPDd');

    const fetchCommissions = async () => {
        try {
            const response = await fetch('http://localhost:8800/commission');
            const data = await response.json();
            return data;
        } catch (error) {
            console.error(error);
            return [];
        }
    };

    useEffect(() => {
        if (map.current) return; // stops map from initializing more than once
    
        map.current = new maplibregl.Map({
            container: mapContainer.current,
            style: `https://api.maptiler.com/maps/streets-v2/style.json?key=${API_KEY}`,
            center: [lng, lat],
            zoom: zoom
        });
    
        // Add control
        map.current.addControl(new maplibregl.NavigationControl(), 'top-right');
    
        // Fetch commissions from your API
        fetch("http://localhost:8800/commission")
            .then(response => response.json())
            .then(commissions => {
                // Loop through commissions and create markers
                commissions.forEach(commission => {
                    new maplibregl.Marker({ color: "#FF0000" })
                        .setLngLat([commission.commissionLongitude, commission.commissionLatitude])
                        .setPopup(new maplibregl.Popup().setHTML(`<h3>${commission.commissionTitle}</h3>`))
                        .addTo(map.current);
                });
            })
            .catch(error => {
                console.error(error);
            });
    }, [API_KEY, lng, lat, zoom]);
    

    return (
        <div className="map-wrap">
            <div ref={mapContainer} className="map" />
        </div>
    )
}
